var fr = JavaImporter(
    org.forgerock.openam.auth.node.api,
    javax.security.auth.callback.TextOutputCallback,
    javax.security.auth.callback.ConfirmationCallback,
    java.util.ArrayList,
    org.forgerock.json.JsonValue,
    java.lang.Integer
);

logger.error("header: " + requestHeaders.get("Accept-Language"));

with (fr) {
    var cbList = ArrayList();
    var jsonMessage = {};

    var acceptLanguageHeader = requestHeaders.get("Accept-Language");

    if (acceptLanguageHeader !== null && acceptLanguageHeader.get(0) == "ar") {
        jsonMessage.msg = ".يرجى التأكد من صحة اسم المستخدم وكلمة المرور ، يرجى المحاولة مرة أخرى";
        jsonMessage.code = 920;
    } else {
        jsonMessage.msg = "Incorrect username or password. Please try again.";
        jsonMessage.code = 919;
    }

    jsonMessage.code = Integer.valueOf(jsonMessage.code);

    if (callbacks.isEmpty()) {
        logger.error("callback empty start");
        cbList.add(new TextOutputCallback(0, JsonValue.json(jsonMessage)));
        cbList.add(new ConfirmationCallback(" ", 0, ["Yes", "No"], 1));
        action = Action.send(cbList).build();
        logger.error("callback empty end");
    } else {
    outcome = "true";
}
}
